import React, { useState } from "react";
import "./SingleContact.css";

const SingleContact = ({ data }) => {
  const [style, setStyle] = useState({ display: "none" });
  const handleOnClickEdit = ({ data }) => {
    if (data) data.email = "something@gmail.com";
  };
  return (
    <div
      onMouseEnter={(e) => {
        setStyle({ display: "block" });
      }}
      onMouseLeave={(e) => {
        setStyle({ display: "none" });
      }}
      className="singleContact-main"
    >
      <div className="singleContact">
        <p>{data.name}</p>
        <p>{data.email}</p>
      </div>
      <div style={style} className="edit-delete">
        <div
          onClick={handleOnClickEdit(data)}
          className="edit-delete-button edit"
        >
          <i className="edit icon"></i>
        </div>
        <div className="edit-delete-button delete">
          <i class="trash alternate icon"></i>
        </div>
      </div>
    </div>
  );
};

export default SingleContact;
