import React, { createContext, useState } from "react";

const AllContacts = createContext();
export default AllContacts;

export const AllContactsProvider = ({ children }) => {
  const [allContacts, setAllContacts] = useState();

  const addUser = async (contactData) => {
    setTimeout(() => {
      setAllContacts(contactData);
      console.log(allContacts);
    }, 2000);
  };

  let contextData = {
    addUser,
    allContacts,
  };

  return (
    <AllContacts.Provider value={contextData}>{children}</AllContacts.Provider>
  );
};
